INSERT INTO `tab_log` VALUES ('13273b80-3232-48cc-bc64-e1896d531442', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"51366329-f0db-4cef-835a-dd4faa79b76c\",\"title\":\"劳动和社会保障部莅临中国农民工政策发源地视察调研\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem5215453371092659\\\" contenteditable=\\\"true\\\"><h4>\\n<span><span><span><span><span><span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 2017年05月24日，劳动和社会保障部党组副书记、常务副部长杨志明率人社部综合处处长刘建国、人社部数据分析处处长李宏、人社部中国劳科院张一名处长一行在市委常委、副市长王兴男、区委副书记汪海波、副区长王巍、团泽镇镇长杨庆前、武装部长李合陪同下来到梦润集团一一中国农民工政策发源地视察调研。张明富汇报了梦润集团在各级党委政府，特别是区委政府和镇党委政府的关心支持下，在山区实现了成功创业。杨部长一行现场视察并听取汇报后，对梦润下乡创业推动脱贫攻坚的实效表示赞扬，对出台农民工政策提供了可靠依据表示感谢，为梦润人扎根山村拔穷根、创业带动就业模式点赞。</span></span></span></span></span></span>\\n\\n</h4><p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-306585143.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544150725216,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 10:45:25');
INSERT INTO `tab_log` VALUES ('135c9cc9-7f02-45af-909c-d8baa59e56cf', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"b2dc319d-34bc-44c7-b0ff-87c36a9b0832\",\"title\":\"海南省琼中县专题培训班到贵州梦润集团参观学习\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem9472399041503181\\\" contenteditable=\\\"true\\\"><h4>\\n<span>2017年07月05日，海南省琼中县的专题培训班到梦润参观学习，晚上体验梦润人自创的烧烤和泡温泉，大家一起载歌载舞，有位老师还为梦润进行曲编制一段舞蹈，感叹在大山里有这样一群人真的很了不起，改变了在农村创业艰辛的模样，原来创业是如此有趣和有激情。</span>\\n\\n</h4><p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-132535447.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544160266117}]', '更新', '2018-12-07 13:24:26');
