INSERT INTO `tab_log` VALUES ('2915e6a9-ca29-4caf-b816-e1ae12a12aec', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"5ef9b144-0d02-4afb-a6a9-ffa4222649d9\",\"title\":\" 遵义市人大副主任刘志义一行亲临梦润公司参观指导\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-153639994.jpg\",\"content\":\"\\n                        <!--可使用 min-height 实现编辑区域自动增加高度-->\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem6225942078464373\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4>\\n\\t2014年 3月27日上午九时许，遵义市人大副主任刘志义一行到梦润公司视察和看望基层人大代表，省人大代表、梦润公司董事长、大坎村村民委员会主任张明富和梦润党支部书记李润开迎接视察，团泽镇人大主席周安国和镇委副书记张维宁陪同接待。</h4>\\n<h4>\\n\\t\\n刘主任一行到了张明富代表回乡创业的梦润源，下车和梦润员工一一的情切握手后，参观了农民工文化长廊、饲料厂、孵化场、食品加工厂、职工大礼堂和避暑旅游客房。又驱车一公里参观了遵义梦润农民工科技创业园（唯一的国家级鹌鹑标准化养殖示范小区）、投资千万元新建的熟化饲料厂、新扩建4800平方米的鹌鹑养殖场建设工地、鹌鹑污水处理厂，同时，参观了团九公路加宽改直施工现场。随后回到梦润支部会议室听取了张明富代表对梦润化妆品系列和鹌鹑产业经营情况，避暑旅游在大坎村起的示范带头作用，对村民致富的拉动，对实现“贵州山区第一村”的推动作用的详细汇报。</h4>\\n\\n<p><br></p></div>\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544163208913}]', '更新', '2018-12-07 14:13:29');
INSERT INTO `tab_log` VALUES ('29bc8b44-d362-4b68-bada-0deb464b0671', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"remarks\":\"\",\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544117998029,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544117998029,\"id\":\"70306447-9c4c-4987-8c3d-6aab3f3406f4\",\"name\":\"信息管理（读）\",\"resourceId\":\"03fc5466-eedf-4444-be87-312d5a71245a\",\"operations\":\"READ\",\"state\":1,\"describe\":\"\"}]', '添加', '2018-12-07 01:39:58');
INSERT INTO `tab_log` VALUES ('2a7a1b14-6c75-43be-bdf5-75b494c7c211', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"f5bf5610-0a6b-4006-9820-288415dd8cb2\",\"title\":\"“工会活力在基层”大型采访活动 媒体记者到梦润集团参观采风\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem35582620613884575\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4 style=\\\"text-align: justify;\\\">\\n\\t<span><span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 2016年11月8日下午14：00至17:30期间，“工会活力在基层”大型采访活动的新闻媒体记者来到梦润集团进行了参观、采风活动。</span></span></h4>\\n<h4 style=\\\"text-align: justify;\\\">\\n\\t<span><span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 采访媒体包括人民网、工人日报、中工网、贵州日报、贵州人民广播电台、多彩贵州网、贵州都市报、贵州商报、贵州交通广播、贵阳电视台新闻联播、劳动时报社、黔工网十二家新闻媒体工作者。媒体一行人首先参观茅台梦润生态猪养殖观光基地和梦润鹌鹑养殖园区。随后乘车来到梦润集团总部，在梦润集团党支部书记张丽鈜的陪同讲解下参观了中国农民工政策发源地陈列馆，在会议室集体观看了梦润集团宣传ppt及视频资料，认真听取了董事长张明富关于梦润文化；梦润集团的发展与精准扶贫之间的联系；梦润集团与沃尔玛签订战略合作；梦润集团与茅台集团合作养殖生态猪的缘由及发展前景；梦润集团下一步将走第一二三产业融合的综合发展模式；各级工会在一个民营企业的发展中起到非常重要的作用六点讲话。</span></span></h4>\\n<h4 style=\\\"text-align: justify;\\\">\\n\\t<span><span>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 参观途中梦润集团提供了特色的烤红薯、炸鹌鹑肉等美食给十二家媒体记者品尝，并祝他们度过愉快的一个记者节，整个参观、采风活动在梦润全体职工热切、积极的配合接待中顺利完成。</span></span></h4>\\n<h4 style=\\\"text-align: justify;\\\">\\n\\t<span><span>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; 为</span><span>工会活力在基层”大型采访活动贵州梦润集团站划上了一个圆满的句号。</span></span></h4>\\n\\n<p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-154723044.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544151016742,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 10:50:17');
