INSERT INTO `tab_log` VALUES ('3b81c6f4-4214-4956-ab67-88b4ab2d3260', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"993ca014-5d57-46dc-bca8-93f75f738327\",\"title\":\"团泽镇巾帼创业带头人共度三八妇女节\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem9087149958484085\\\" contenteditable=\\\"true\\\"><h4>\\n<span> &nbsp;&nbsp;&nbsp; 2018年3月8日，团泽镇“优秀妇女干部”表彰大会在镇政府召开，梦润集团财务总监李润开获“巾帼创业带头人”荣誉证书并在大会发言。大会过后，妇女同胞们向中国农民工政策发源地出发，前往中国农民工文化陈列馆参观学习。</span></h4><h4>\\n<span>&nbsp;&nbsp;&nbsp; 一路上，妇女同胞们听返乡创业女大学生陈月琴讲解梦润人下乡创业事迹，学习参观购买梦润集团生态绿色卤鹌鹑蛋、鹌鹑肉辣椒和还原土猪鲊肉等绿色产品。一行人还参观了中国农民工文化陈列馆、梦润集团党建馆、全国劳模张明富创新工作室，聆听劳模故事，学习劳模精神。</span></h4><h4>\\n<span>&nbsp;&nbsp;&nbsp; 巾帼创业带头人、贵州省妇联执委、汇川区人大代表、梦润集团财务总监李润开2018年寄语：在中央提出乡村振兴战略进程中，希望妇女姐妹们积极参与和关心农民工、大学生等下乡创业，我坚信我们妇女一定会在乡村振兴伟大事业中发挥更大作用。祝我们所有妇女同胞们节日快乐！</span></h4>\\n\\n<p><br></p>\\n\\n<p><br></p><p><br></p></div>\",\"imgs\":\"http://www.gzmrjt.com/uploads/180703/5-1PF3210204303.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544152921531,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 11:22:02');
INSERT INTO `tab_log` VALUES ('3cb28ea4-0833-41c1-99e5-07399094d144', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"e1db5283-19f6-486a-aab3-7651a654c8cb\",\"title\":\"贵州省总工会到梦润集团参观学习\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem29027066642569677\\\" contenteditable=\\\"true\\\"><h4>\\n<span>&nbsp;&nbsp;&nbsp; 2018年6月27日，贵州省总工会来到中国农民工政策发源地--贵州省遵义市汇川区团泽镇大坎村凉风沟梦润农民工返乡下乡创业园参观学习。</span></h4>\\n<h4><span>&nbsp;&nbsp;&nbsp; 梦润集团总经理、党支部书记张丽鈜担任解说员，引领贵州省总工会参观了国家级梦润标准化养殖示范区，茅台集团梦润还原土猪养殖观光示范基地，中国农民工文化陈列馆，梦润集团党建馆，全国劳模张明富创新工作室等。</span></h4>\\n\\n<p><br></p><p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-147186397.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544158859562}]', '更新', '2018-12-07 13:01:00');
