INSERT INTO `tab_log` VALUES ('6359a70b-2f83-46c2-a90d-f1e4a717b431', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"f1955528-afe4-496b-9243-168fa206eaee\",\"title\":\"瓮福集体后勤保障部总经理毛远富带队参观梦润公司\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem5633886304130585\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4>&nbsp;\\n\\t2015年3月14日上午11时许，瓮福集体后勤保障部总经理带队到我公司参观学习，随同人员有王之贵、李平静、李华菊、邓林、李华兰。</h4><h4>&nbsp;\\n\\t办公室人员张丽鈜引领毛远富经理一行参观梦润农民工科技创业园全自动鹌鹑标准化养殖园区，新建全自动标准养殖房、污水处理厂、鹌鹑粪便肥料发酵车间和新扩建的熟化饲料厂等。</h4>\\n<h4>\\n\\t随后一行人来到梦润源，公司员工张丽鈜对源区农民工政策发源地、公司发展概况、公司文化进行了介绍。</h4>\\n\\n<p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-149608287.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544160433746}]', '更新', '2018-12-07 13:27:14');
