INSERT INTO `tab_log` VALUES ('b9c8446c-c520-4195-90b4-d6ec51284592', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"7b5dff69-42c6-4409-aae4-d695d7a9f690\",\"title\":\"贵州省人大副主任周忠良等领导一行莅临梦润集团调研指导\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem6318841326158694\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4>\\n\\t<span><span>201</span></span><span><span>6</span></span><span><span>年</span></span><span><span>10</span></span><span><span>月2</span></span><span><span>1</span></span><span><span>日</span></span><span><span>上午</span></span><span><span>，</span></span><span>贵州省人大副主任周忠良一行在</span><span>遵义</span><span><span>市人大副主任郭世刚、汇川区人大副主任谢世平等</span></span><span><span>领导</span></span><span><span>的陪同下</span></span><span><span>分别</span></span><span><span>视察了贵州梦</span></span><span><span>润鹌鹑生态养殖</span></span><span><span>园</span></span><span><span>、茅台集团梦润生态猪养殖观光基地</span></span><span><span>和梦润集团总部。</span></span><span>周忠良主任对梦润</span><span>集团</span><span>的发展变化很满意，候主任还专门了解了在他督办下张明富代表建议的落实情况</span><span>，对</span><span>人大代表张明富的返乡创业对脱贫攻坚起到的巨大作用给予了高度评价与肯定，对梦润的发展提出</span><span>了</span><span>切实建议并对梦润的未来寄予厚望。</span></h4>\\n\\n<p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-133018622.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544160339704}]', '更新', '2018-12-07 13:25:40');
