INSERT INTO `tab_log` VALUES ('cc794624-cf20-4c2a-a157-98bc6c9c5402', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"8b20a4a6-e2b1-413c-ac87-17442320bbd2\",\"title\":\"团泽镇团泽小学师生代表走进中国农民工政策发源地创业培训基地\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem2643372159517796\\\" contenteditable=\\\"true\\\"><h4>\\n<span>&nbsp;&nbsp;&nbsp; 3月14日，团泽镇团泽小学组织优秀学生代表到中国农民工政策发源地参观学习梦润集团返乡下乡创业事迹。</span></h4>\\n<h4><span>&nbsp;&nbsp;&nbsp; 同学们认真聆听返乡创业女大学生陈月琴讲解张明富下乡创业扶贫事迹。听完全国劳模、返乡下乡创业农民工、共产党员张明富抡起铁锤砸向贫困的事迹后，同学们纷纷表示要和这位伟人合影，要向他学习艰苦奋斗的劳模精神。</span></h4>\\n<h4><span>&nbsp;&nbsp;&nbsp; 参观中国农民工文化陈列馆，了解中国农民工政策发源地的由来，深入学习梦润集团董事长张明富给两任总理的信件内容及总理的来信批示。梦润集团董事长张明富向小学生们讲解当时自己为什么要给两位总理写信，鼓励小学生们好好学习，树立理想和奉献精神，反哺家乡，振兴乡村。</span></h4>\\n\\t\\n\\n<p><br></p>\\n\\n<p><br></p><p><br></p></div>\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544155458007,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 12:04:18');
