INSERT INTO `tab_log` VALUES ('cf3d4641-c349-455e-a700-8ed5d4c85d68', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"6475a133-d8d7-4d6a-8f1d-bede824a0027\",\"title\":\"贵州梦润集团董事长张明富应邀参加第三届农民工节\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem9738118862770643\\\" contenteditable=\\\"true\\\"><h4>\\n<span>2017年05月24日，第三届农民工节在贵州省遵义市正安县隆重举行，劳动和社会保障部党组副书记、副部长杨志明和副省长陈明鸣、市委常委副市长王兴男出席，梦润集团董事长张明富应邀参加并与杨部长同台论道。</span>\\n\\n</h4><p><br></p></div>\",\"imgs\":\"http://www.gzmrjt.com/uploads/allimg/171112/10252TM4-0.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544160177106}]', '更新', '2018-12-07 13:22:57');
INSERT INTO `tab_log` VALUES ('cfd6288e-86fa-45ce-92b2-b5a97971682f', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"2c9c873e-35f6-4384-a58c-e3379d6b0ccd\",\"title\":\" 广西壮族自治区第一书记培训班在梦润集团培训圆满结束 \",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-164838269.jpg\",\"content\":\"\\n                        <!--可使用 min-height 实现编辑区域自动增加高度-->\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem6326454874171288\\\" contenteditable=\\\"true\\\"><h4>请在此编辑学员学习照内容</h4><p><br></p></div>\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544161963579,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 13:52:44');
INSERT INTO `tab_log` VALUES ('d10ce5f6-cc5c-40fb-a4c4-92b9bf7fa921', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"86fced7a-bf09-4ca7-ac61-604b137bae68\",\"title\":\"抢抓发改委结对帮扶契机 搭建农民工返乡创业大平台\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem855688658635818\\\" contenteditable=\\\"true\\\"><h4>\\n<span><span><span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 为了认真贯彻落实习近平总书记提出的“中国要美，农村必须美；中国要强，农业必须强；中国要富，农民必须富”和李克强总理提出的“要像解决自己亲人问题一样，想方设法帮助农民工返乡创业就业......”的重要指示精神，以及国办发2015〔47号〕《关于支持农民工等人员返乡创业的意见》政策要求，结合“大众创业，万众创新”和贵州省的“雁归兴贵”行动，省发改委第六党支部与梦润公司和大坎村签订了结对共建小康协议。签约目标和办法：省发改委第六党支部通过支持以张明富的梦润企业为基础，建设“贵州梦润农民工返乡创业园平台”，产生更强的造血功能，促进集体经济发展解决精准扶贫问题，带动大坎村到\\n 2020 年实现更高水平的全面小康。签约帮扶时间，2015年至2020年12月30日。</span></span></span>\\n\\n</h4><p><br></p></div>\",\"imgs\":\"http://www.gzmrjt.com/uploads/allimg/171111/1-1G1111G91A62.png\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544152585278,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 11:16:25');
