INSERT INTO `tab_log` VALUES ('e5f71c96-f641-40ec-87dd-6bc258d5b8e6', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"7b5dff69-42c6-4409-aae4-d695d7a9f690\",\"title\":\"贵州省人大副主任周忠良等领导一行莅临梦润集团调研指导\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem6318841326158694\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4>\\n\\t<span><span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 201</span></span><span><span>6</span></span><span><span>年</span></span><span><span>10</span></span><span><span>月2</span></span><span><span>1</span></span><span><span>日</span></span><span><span>上午</span></span><span><span>，</span></span><span>贵州省人大副主任周忠良一行在</span><span>遵义</span><span><span>市人大副主任郭世刚、汇川区人大副主任谢世平等</span></span><span><span>领导</span></span><span><span>的陪同下</span></span><span><span>分别</span></span><span><span>视察了贵州梦</span></span><span><span>润鹌鹑生态养殖</span></span><span><span>园</span></span><span><span>、茅台集团梦润生态猪养殖观光基地</span></span><span><span>和梦润集团总部。</span></span><span>周忠良主任对梦润</span><span>集团</span><span>的发展变化很满意，候主任还专门了解了在他督办下张明富代表建议的落实情况</span><span>，对</span><span>人大代表张明富的返乡创业对脱贫攻坚起到的巨大作用给予了高度评价与肯定，对梦润的发展提出</span><span>了</span><span>切实建议并对梦润的未来寄予厚望。</span></h4>\\n\\n<p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-147426269.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544151329905,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 10:55:30');
INSERT INTO `tab_log` VALUES ('e6f2695f-0a8a-468c-a03c-f4fe59837747', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"3f8c0a90-1112-445d-8083-7074c2e5d05b\",\"title\":\"张明富参加首届全国新农民新技术创业创新博览会 获中共中央政治\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem9760406240945522\\\" contenteditable=\\\"true\\\"><h4>&nbsp;\\n<span>&nbsp;&nbsp;&nbsp; 2017年11月10日下午，首届全国新农民新技术创业创新博览会在苏州召开，贵州省遵义市汇川区返乡创业农民工代表张明富受邀参加博览会，并作为全国典型在大会上作代表发言。中共中央政治局常委、国务院副总理汪洋出席大会并讲话。</span></h4>\\n<h4><span>&nbsp;&nbsp;&nbsp;&nbsp; 汪洋巡馆参观了全国农村创业创新成果展，汪副总理来到该成果展的“能人引路雁归逐梦“展区，来到贵州张明富展示牌前，与张明富亲切握手。</span></h4><h4><span></span></h4><h4><span>\\n\\t&nbsp; &nbsp; &nbsp; \\n&nbsp;张明富激动地向汪洋总理介绍了创业事迹，说到:“我是革命老区贵州遵市义市汇川区的返乡下乡创业农民工张明富。曾经在省城和广东打工，在具备了城市创业发展条件后，回到当地人都弃而远之的穷乡僻壤大坎村凉风沟进行创业，先开化妆品厂和鹌鹑产业化经营，还与茅台集团合资，用野生猪草加玉米煮熟了养360天的还原土猪，实现了规模养猪零排放，帮助了很多贫困户脱贫。”还介绍了汇川区支持农民工返乡创业的政策优惠，以及首届中国农民工创业创新论坛的情况。</span></h4>\\n\\t&nbsp;\\n\\n<p><br></p><p><br></p></div>\",\"imgs\":\"http://www.gzmrjt.com/uploads/allimg/180620/5-1P620152245958.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544152751992,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 11:19:12');
