INSERT INTO `tab_log` VALUES ('ec62d955-51dc-4c5a-abdd-c07caa13d6ff', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"36f9a0a4-9e03-4da1-b1ac-05b1dc4dd44f\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-169032922.jpg\",\"describe\":\"公司融乡村旅游、高端养老、养生怡养、生态农业为一体，将传统的养老、旅游等行业高度整合\",\"url\":\"http://www.zmftour.com/\",\"start\":\"2018-12-10\",\"end\":\"2018-12-21\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544159347332,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 13:09:07');
INSERT INTO `tab_log` VALUES ('ecf2a31f-33c9-4fdc-ab93-763a1d7cc6d9', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"b8dc9ffd-84ca-40d8-800d-236d5f56988a\",\"title\":\"广西壮族自治区第一书记培训班在梦润集团培训圆满结束\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem8828520586608627\\\" contenteditable=\\\"true\\\"><h4>\\n<span>2017年05月26日，贵州省委组织部干部学院组织广西自治区，第二期第一书记培训班100名学员在“中国农民工政策发源地”一一梦润集团的培训班今天下午圆满结束，同学们实地参观了茅台集团梦润还原土猪养殖观光基地、梦润国家级鹌鹑标准养殖示范区、农民工返乡下乡创业培训基地、汇川区农民工陈列馆。并进行讲课和会场座谈，广西干部纷纷提问交流，学员们对梦润集团在各级党委政府关怀支持下，在汇川区委政府和镇党委政府的领导下，梦润返乡下乡创业带动大坎村新农村发展取得的成效非常赞赏。会后很多学员单独进行合作对接，现场购买了梦润系列日用化妆品和鹌鹑系列食品，为黔货出山开僻了新路经，几位广西干部&nbsp;还特别对遵义市汇川区农民工返乡下乡创业培训基地、汇川区军地两用人才培训基地、贵州省总工会干部学校教育培训基地等单位在这里挂牌的方式很认可和点赞。</span>\\n\\n</h4><p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-150097134.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544160273247}]', '更新', '2018-12-07 13:24:33');
INSERT INTO `tab_log` VALUES ('eee90b32-90aa-4140-9d52-4dbd8f4c82b9', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在添加数据，数据信息为：\n[{\"id\":\"8a36d1ac-515d-4254-8f3a-34ef426ee393\",\"title\":\"福泉市仙桥乡中心学校校（园）长 与梦润公司学习交流会\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem31227997233426497\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4>\\n\\t\\n&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 2015年3月14日上午10时许，经蔡顺华教授介绍福泉市仙桥乡中心小学周进明校长带队，随同福泉市仙桥乡谷顶招小学余贵海校长，兰卡小学秋金田主任，大麻窝小学周长宝校长，中心小学周业明校长，幼儿园刘巧校长\\n ，蛤蚌河小学蒲华校长，粑粑菁小学兰兴彬校长，中兴学校刘胜洪校长，前来学习考察，并请张明富董事长上课。</h4>\\n<h4>\\n\\t&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 办公室人员张丽鈜引领周进明校长一行参观梦润农民工科技创业园全自动鹌鹑标准化养殖园区，新建全自动标准养殖房、污水处理厂、鹌鹑粪便肥料发酵车间和新扩建的熟化饲料厂等。</h4>\\n<h4>\\n\\t&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 随后一行人来到梦润源，公司员工张丽鈜对源区农民工政策发源地、公司发展概况、公司文化进行了介绍。</h4>\\n\\n<p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-164226185.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544151479440,\"updateUserId\":null,\"updateDate\":null}]', '添加', '2018-12-07 10:57:59');
INSERT INTO `tab_log` VALUES ('f402e794-9f43-4e44-85bf-9f4320677f7c', 'd6a00d9d-2801-45c3-a99f-326e2949e2f2', '用户：【admin@qq.com】正在更新数据，数据信息为：\n[{\"id\":\"1e373cb5-5074-41cf-ac0e-86e3eb417ba2\",\"title\":\"习水下乡创业学习考察团到梦润集团，向梦润集团董事长张明富学习\",\"content\":\"\\n                        \\n                    <div style=\\\"width:100%; height:100%;\\\" class=\\\"w-e-text\\\" id=\\\"text-elem14293728865006594\\\" contenteditable=\\\"true\\\"><h4>\\n</h4><h4><span style=\\\"font-weight: normal;\\\">&nbsp;3月11日至12日，习水创业学习考察团到中国农民工政策发源地考察学习，并达成下乡创业意向，并想加盟梦润集团，在当地农村复制张明富下乡创业扶贫模式，与梦润合作开拓市场。</span></h4><h4>\\n\\t&nbsp;\\n\\n</h4><p><br></p></div>\",\"imgs\":\"https://goss.veer.com/creative/vcg/veer/800water/veer-317669008.jpg\",\"remarks\":null,\"createUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"createDate\":1544112000000,\"updateUserId\":\"d6a00d9d-2801-45c3-a99f-326e2949e2f2\",\"updateDate\":1544160451420}]', '更新', '2018-12-07 13:27:31');
